mapper.select:
select
  log_id, log_name, business_type, method_name, request_method, oper_name, oper_type, oper_ip, oper_url, oper_status, error_msg, oper_time
from myblog.sys_log


        and log_id = #{logId}


        and log_name = #{logName}


        and business_type = #{businessType}


        and method_name = #{methodName}


        and request_method = #{requestMethod}


        and oper_name = #{operName}


        and oper_type = #{operType}


        and oper_ip = #{operIp}


        and oper_url = #{operUrl}


        and oper_status = #{operStatus}


        and error_msg = #{errorMsg}


        and oper_time = #{operTime}
